import React, { useState,useRef } from "react";
import Header from "./component/Header";
import Itemlist from "./component/Itemlist";
import Total from "./component/Total";




function App() {
  const [itemList,updateItem]=useState([])
  const itemNameVal=useRef()
  const itemPriceVal=useRef()

  function addList(e){

    const itemName=itemNameVal.current.value
    const itemPrice=itemPriceVal.current.value
    console.olo
    if(itemName== "" || parseFloat(itemPrice)===NaN ) return

    updateItem(prevItem=>{
      return [...prevItem,{id:itemName,name:itemName,price:itemPrice,quantity:1}]
    })
    itemNameVal.current.value=null
    itemPriceVal.current.value=null
  }



  return (

    <div className="container">
      <Header/>
      <Itemlist itemList={itemList} />
      <Total Total={itemList}/>
      <input type="text" ref={itemNameVal}/>
      <input type="text" ref={itemPriceVal}/>
      <button onClick={addList}>Add</button>


    </div>
  );
}


export default App;
