import { useState } from "react"
import React from 'react'
import PropTypes from 'react'


function Itemlist({itemList}) {
  updateItem(prevItem)

 
  return itemList.map(item=>{
    return (
      <div key={item.name}>
      <p>{item.name}</p>
      <p>{item.price}</p>
      <div>
        Quantity:1
      </div>
      <button onClick={removeItem}>X</button>
      
      </div>
       
    )
   
  })

}



export default Itemlist
