import React from 'react'





function Itemlist({itemList}) {
  const[list,removed]=useState(itemList)

  function removeItem(item){
    console.log(item.id)
    

  }
  

 
  return itemList.map(item=>{
    return (
      <div key={item.name}>
      <p>{item.name}</p>
      <p>{item.price}</p>
      <div>
        Quantity:1
      </div>
      <button onClick={removeItem()} id={item.id} className="removebtn">X</button>
      
      </div>
       
    )
   
  })

}



export default Itemlist
