import React ,{useState} from 'react'
import {FaPlus} from 'react-icons/fa'
import { v4 as uuidv4 } from 'uuid';




function Sidebar({setSitebar,itemList,updateItem}) {
    console.log(itemList)
    const [savedList,updateList]=useState([])
   console.log(savedList)

    function updateSidebar(){
        setSitebar(state=>{
            return !state
        })
    }
    function saveList(){
        if(itemList.length==0)return
        updateList(prevlist=>{
            return[...prevlist,{id:uuidv4(),name:"list"+(savedList.length+1),data:itemList}]
        })
        
        updateItem([])
        

    }



  return (
    
    <div className='sidebar-cont'>
        <div className="sidebar-header">
            <button onClick={updateSidebar}>
                <FaPlus size="20px"/>
            </button>
        </div>
        <div className='saved-list'>Saved list
        {savedList.map(item=>{
            return (
                 <div className='' key={item.id}>
                     {item.name}

                </div>)
        })}
        </div>
        <button onClick={saveList}>Save Current List</button>
    </div>
    
  )
}

export default Sidebar
