      return total+(item.price*item.quantity)

    },0)

   
    return(
        <div className="total-container">
             <p >{itemTotal===0? "No item ":"Total :"}</p>
             <span>{itemTotal===0? "":"RM"+itemTotal}</span>
        </div>
    )


        
  
 


 
}

export default Total
