import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'





function Itemlist({itemList,updateQuantity}) {



   function updateItemQuantity(id,operation){
   updateQuantity(id,operation)
  }

 

  return itemList.map(item=>{
    return (
      <div key={item.id} className="item">
      <p>{item.name}</p>
      <div className='item-price'>
        <p>{item.price}</p>
        <div>
          <button onClick={()=>updateItemQuantity(item.id,"plus")}className="btn-plus"><FontAwesomeIcon icon={["fas","plus"]}/></button> 
          <span className='item-quantity'>{item.quantity}</span>
          <button onClick={()=>updateItemQuantity(item.id,"minus")} className="btn-plus">x</button> 

        </div>
        
        
      </div>  
      </div>
       
    )
   
  })

}



export default Itemlist
