import React from 'react'





function Itemlist({itemList,removeItem}) {

 function removeListItem(id){
   removeItem(id)
    

  }

 
  return itemList.map(item=>{
    return (
      <div key={item.name}>
      <p>{item.name}</p>
      <p>{item.price}</p>
      <div>
        Quantity:1
      </div>
      <input type="checkbox" onChange={removeListItem(item)} />
      
      </div>
       
    )
   
  })

}



export default Itemlist
