import Header from "./Header"
import { useState } from "react"




const Task = () => {
    const [task,setTask]=useState( [{   //task as the const name 
                                        //setS
        id:1,
        text:"Doctor Appointment",
        day:"Feb 5th at 2:30pm",
        reminder:true  

    },
    {
        id:2,
        text:"Meeting at school",
        day:"Feb 56th at 1:30pm",
        reminder:true  

    },
    {
        id:3,
        text:"Food Shopping",
        day:"Feb 7th at 2:30pm",
        reminder:false  

    }])
  return(
      <>
      {task.map(task=>( 
      <h3 key={task.id}>{task.text}</h3> 
      
      ))}
      </>
  )
  
}

export default Task
